#ifndef ONLINE_JUDGE
  cerr << "Code by H~$~C: \n" << flush;
#endif
  ios_base::sync_with_stdio(false);
  cin.tie(nullptr), cout.tie(nullptr);
  cout << fixed << setprecision(12);
} } __starter_of_program__;

struct __ENDDER_OF_PROGRAM__ { ~__ENDDER_OF_PROGRAM__() {
  cerr << "

Time elasped: " << clock() * 1000.0 / CLOCKS_PER_SEC << " ms
" << flush;
} } __endder_of_program__;

function.block:
{
  read(n, m, k);
  if (k == 0) {
    writeln("Yes");
    return 0;
  }
  rep(i, k) {
    int x, y;
    read(x, y);
    --x, --y;
    v.pb({x, y});
    row[x].pb(y);
    col[y].pb(x);
  }
  if (k + n >= 1LL * n * m) {
    vector<vector<bool> > vis(n, vector<bool>(m, 0));
    rep(i, k) vis[v[i].fi][v[i].se] = true;
    ll cnt = 0; 
    rep(i, n) {
      if (!vis[i][0]) ++cnt;
      else break;
    }
    if (cnt == 1LL * n * m - k) {
      writeln("Yes");
      return 0;
    }
  }
  rep(i, n) {
    sort(row[i].begin(), row[i].end());
    sort(col[i].begin(), col[i].end());
  }
  int f = 0;
  int x = 0, y = 0;
  ll tot = 1;
  while (true) {
    if (f % 2 == 0) {
      rs.insert(x);
      vis[x][0] = true;
      int nx, ny;
      if (f == 0) {
        auto it = lower_bound(row[x].begin(), row[x].end(), y); 
        if (it == row[x].end())
          nx = x, ny = m - 1;
        else nx = x, ny = *it - 1;
        auto its = cs.lower_bound(y + 1);
        if (its != cs.end()) ny = min(ny, *its - 1);
      }
      else {
        auto it = upper_bound(row[x].begin(), row[x].end(), y);
        if (it == row[x].begin())
          nx = x, ny = 0;
        else nx = x, ny = *prev(it) + 1;
        auto its = cs.upper_bound(y - 1);
        if (its != cs.begin()) ny = max(ny, *prev(its) + 1);
      }
      if (ny == y) break;
      tot += abs(x - nx) + abs(y - ny);
      x = nx, y = ny;
      if (vis[y][1]) break;
    }
    else {
      cs.insert(y);
      vis[y][1] = true;
      int nx, ny;
      if (f == 1) {
        auto it = lower_bound(col[y].begin(), col[y].end(), x);
        if (it == col[y].end())
          nx = n - 1, ny = y;
        else nx = *it - 1, ny = y;
        auto its = rs.lower_bound(x + 1);
        if (its != rs.end()) nx = min(nx, *its - 1);
      }
      else {
        auto it = upper_bound(col[y].begin(), col[y].end(), x);
        if (it == col[y].begin())
          nx = 0, ny = y;
        else nx = *prev(it) + 1, ny = y;
        auto its = rs.upper_bound(x - 1);
        if (its != rs.begin()) nx = max(nx, *prev(its) + 1);
      }
      if (nx == x) break;
      tot += abs(x - nx) + abs(y - ny);
      x = nx, y = ny;
      if (vis[x][0]) break;
    }
//    Debug(x, y, tot);
    f = (f + 1) % 4;
  }
  if (tot >= 1LL * n * m - k)
    writeln("Yes");
  else writeln("No");
	return 0;
}
