function.block:
{
    SPEED
    int N; cin >> N;
    vector<vector<ll>> c(3,vector<ll>(N));
    for(int i = 0; i < N; ++i) {
        cin >> c[0][i];
    }
    for(int i = 0; i < N; ++i) {
        cin >> c[1][i];
    }
    for(int i = 0; i < N; ++i) {
        cin >> c[2][i];
    }
    vector<vector<int>> edge(N);
    vector<int> cnt(N);
    for(int i = 0; i < N-1; ++i) {
        int a,b; cin >> a >> b;
        a--,b--;
        cnt[a]++,cnt[b]++;
        edge[a].push_back(b);
        edge[b].push_back(a);
    }
    for(int i = 0; i < N; ++i) corner(cnt[i]>=3,-1);

    int root;
    for(int i = 0; i < N; ++i) if(cnt[i]==1) root = i;
    vector<ll> label(N,-1),ans;
    ll sum = 0,mini = HIGHINF;
    queue<int> q;

    for(int i = 0; i < N; ++i) label[i] = -1;
    q.push(root);
    label[root] = 0;
    while(q.size()) {
        int from = q.front();
        q.pop();
        for(int to:edge[from]){
            if(label[to]!= -1) continue;
            label[to] = (label[from]+1) % 3;
            q.push(to);
        }
    }
    sum = 0;
    for(int i = 0; i < N; ++i) sum += c[label[i]][i];
    if(sum < mini) {
        mini = sum;
        ans = label;
    }

    for(int i = 0; i < N; ++i) label[i] = -1;
    q.push(root);
    label[root] = 0;
    while(q.size()) {
        int from = q.front();
        q.pop();
        for(int to:edge[from]){
            if(label[to]!= -1) continue;
            label[to] = (label[from]+2) % 3;
            q.push(to);
        }
    }
    sum = 0;
    for(int i = 0; i < N; ++i) sum += c[label[i]][i];
    if(sum < mini) {
        mini = sum;
        ans = label;
    }

    for(int i = 0; i < N; ++i) label[i] = -1;
    q.push(root);
    label[root] = 1;
    while(q.size()) {
        int from = q.front();
        q.pop();
        for(int to:edge[from]){
            if(label[to]!= -1) continue;
            label[to] = (label[from]+1) % 3;
            q.push(to);
        }
    }
    sum = 0;
    for(int i = 0; i < N; ++i) sum += c[label[i]][i];
    if(sum < mini) {
        mini = sum;
        ans = label;
    }

    for(int i = 0; i < N; ++i) label[i] = -1;
    q.push(root);
    label[root] = 1;
    while(q.size()) {
        int from = q.front();
        q.pop();
        for(int to:edge[from]){
            if(label[to]!= -1) continue;
            label[to] = (label[from]+2) % 3;
            q.push(to);
        }
    }
    sum = 0;
    for(int i = 0; i < N; ++i) sum += c[label[i]][i];
    if(sum < mini) {
        mini = sum;
        ans = label;
    }

    for(int i = 0; i < N; ++i) label[i] = -1;
    q.push(root);
    label[root] = 2;
    while(q.size()) {
        int from = q.front();
        q.pop();
        for(int to:edge[from]){
            if(label[to]!= -1) continue;
            label[to] = (label[from]+1) % 3;
            q.push(to);
        }
    }
    sum = 0;
    for(int i = 0; i < N; ++i) sum += c[label[i]][i];
    if(sum < mini) {
        mini = sum;
        ans = label;
    }

    for(int i = 0; i < N; ++i) label[i] = -1;
    q.push(root);
    label[root] = 2;
    while(q.size()) {
        int from = q.front();
        q.pop();
        for(int to:edge[from]){
            if(label[to]!= -1) continue;
            label[to] = (label[from]+2) % 3;
            q.push(to);
        }
    }
    sum = 0;
    for(int i = 0; i < N; ++i) sum += c[label[i]][i];
    if(sum < mini) {
        mini = sum;
        ans = label;
    }
    cout << mini << endl;
    for(int i = 0; i < N; ++i) {
        cout << ans[i]+1 << " ";
    }
    cout << endl;

    return 0;
}
