function.block:
{
	int x = 0, f = 1;
	char c = getchar();
	while (c < '0' || c > '9')
	{
		if (c == '-') f = -1;
		c = getchar();
	}
	while (c >= '0' && c <= '9')
	{
		x = x * 10 + c - '0';
		c = getchar();
	}
	return x * f;
}

{
	ll x = 0, f = 1;
	char c = getchar();
	while (c < '0' || c > '9')
	{
		if (c == '-') f = -1;
		c = getchar();
	}
	while (c >= '0' && c <= '9')
	{
		x = x * 10 + c - '0';
		c = getchar();
	}
	return x * f;
}

{
	if (x < 0)
	{
		putchar('-');
		x = -x;
	}
	int y = 10, len = 1;
	while (y <= x)
	{
		y = (y << 3) + (y << 1);
		len++;
	}
	while (len--)
	{
		y /= 10;
		putchar(x / y + 48);
		x %= y;
	}
}

{
	if (x < 0)
	{
		putchar('-');
		x = -x;
	}
	ll y = 10, len = 1;
	while (y <= x)
	{
		y = (y << 3) + (y << 1);
		len++;
	}
	while (len--)
	{
		y /= 10;
		putchar(x / y + 48);
		x %= y;
	}
}

{
	pint(x);
	putchar('\n');
}

{
	pll(x);
	putchar('\n');
}

{            //å¤æ­xæ¯å¦ç­äº0
	if (fabs(x) < eps)
		return 0;
	else
		return x < 0 ? -1 : 1;
}

{  //æ¯è¾ä¸¤ä¸ªæµ®ç¹æ°ï¼0 ç¸ç­ï¼-1 å°äºï¼1 å¤§äº
	if (fabs(x - y) < eps)
		return 0;
	else
		return x < y ? -1 : 1;
}

unit.struct:
struct Point
{  //å®ä¹ç¹ååºæ¬è¿ç®
	double x, y;
	Point()
	{
	}
	Point(double x, double y) : x(x), y(y)
	{
	}
	Point operator+(Point B)
	{
		return Point(x + B.x, y + B.y);
	}
	Point operator-(Point B)
	{
		return Point(x - B.x, y - B.y);
	}
	Point operator*(double k)
	{
		return Point(x * k, y * k);
	}  //é¿åº¦å¢å¤§kå
	Point operator/(double k)
	{
		return Point(x / k, y / k);
	}  //é¿åº¦ç¼©å°kå
	bool operator==(Point B)
	{
		return sgn(x - B.x) == 0 && sgn(y - B.y) == 0;
	}
	bool operator<(Point B)
	{
		return sgn(x - B.x) < 0 || (sgn(x - B.x) == 0 && sgn(y - B.y) < 0);
	}  //ç¨äºå¸å
};

function.block:
{
	return A.x * B.x + A.y * B.y;
}

{
	return sqrt(Dot(A, A));
}

{
	return Dot(A, A);
}

{
	return acos(Dot(A, B) / Len(A) / Len(B));
}

{
	return A.x * B.y - A.y * B.x;
}

{
	return Cross(B - A, C - A);
}

{
	return hypot(A.x - B.x, A.y - B.y);
}

{
	return sqrt((A.x - B.x) * (A.x - B.x) + (A.y - B.y) * (A.y - B.y));
}

{
	return Vector(-A.y / Len(A), A.x / Len(A));
}

{
	return sgn(Cross(A, B)) == 0;
}

{  //åéAéæ¶éæè½¬radåº¦
	return Vector(A.x * cos(rad) - A.y * sin(rad),
		A.x * sin(rad) + A.y * cos(rad));
}

unit.struct:
struct Line
{
	Point p1, p2;  //çº¿ä¸çä¸¤ä¸ªç¹
	Line()
	{
	}
	Line(Point p1, Point p2) : p1(p1), p2(p2)
	{
	}
	// Line(Point x,Point y){p1 = x;p2 = y;}
	//  Point(double x,double y):x(x),y(y){}
	//æ ¹æ®ä¸ä¸ªç¹åå¾æè§ angle ç¡®å®ç´çº¿,0<=angle<pi
	Line(Point p, double angle)
	{
		p1 = p;
		if (sgn(angle - pi / 2) == 0)
		{
			p2 = (p1 + Point(0, 1));
		}
		else
		{
			p2 = (p1 + Point(1, tan(angle)));
		}
	}
	// ax+by+c=0
	Line(double a, double b, double c)
	{
		if (sgn(a) == 0)
		{
			p1 = Point(0, -c / b);
			p2 = Point(1, -c / b);
		}
		else if (sgn(b) == 0)
		{
			p1 = Point(-c / a, 0);
			p2 = Point(-c / a, 1);
		}
		else
		{
			p1 = Point(0, -c / b);
			p2 = Point(1, (-c - a) / b);
		}
	}
};

function.block:
{
	double k = atan2(v.p2.y - v.p1.y, v.p2.x - v.p1.x);
	if (sgn(k) < 0) k += pi;
	if (sgn(k - pi) == 0) k -= pi;
	return k;
}

{
	int c = sgn(Cross(p - v.p1, v.p2 - v.p1));
	if (c < 0) return 1;  // 1ï¼på¨vçå·¦è¾¹
	if (c > 0) return 2;  // 2ï¼på¨vçå³è¾¹
	return 0;             // 0ï¼på¨vä¸
}

{
	return sgn(Cross(p - v.p1, v.p2 - v.p1)) == 0 &&
		sgn(Dot(p - v.p1, p - v.p2)) <= 0;
}

{
	if (sgn(Cross(v1.p2 - v1.p1, v2.p2 - v2.p1)) == 0)
	{
		if (Point_line_relation(v1.p1, v2) == 0)
			return 1;  // 1 éå
		else
			return 0;  // 0 å¹³è¡
	}
	return 2;  // 2 ç¸äº¤
}

{
	return fabs(Cross(p - v.p1, v.p2 - v.p1)) / Distance(v.p1, v.p2);
}

{
	double k = Dot(v.p2 - v.p1, p - v.p1) / Len2(v.p2 - v.p1);
	return v.p1 + (v.p2 - v.p1) * k;
}

{
	Point q = Point_line_proj(p, v);
	return Point(2 * q.x - p.x, 2 * q.y - p.y);
}

{  // Line1:ab,  Line2:cd
	double s1 = Cross(b - a, c - a);
	double s2 = Cross(b - a, d - a);  //åç§¯ææ­£è´
	return Point(c.x * s2 - d.x * s1, c.y * s2 - d.y * s1) / (s2 - s1);
}

{  // Line1:ab,  Line2:cd
	double c1 = Cross(b - a, c - a), c2 = Cross(b - a, d - a);
	double d1 = Cross(d - c, a - c), d2 = Cross(d - c, b - c);
	return sgn(c1) * sgn(c2) < 0 &&
		sgn(d1) * sgn(d2) < 0;  //æ³¨æäº¤ç¹æ¯ç«¯ç¹çæåµä¸ç®å¨å
}

unit.struct:
struct Polygon
{
	int n;          //å¤è¾¹å½¢çé¡¶ç¹æ°
	Point p[maxp];  //å¤è¾¹å½¢çç¹
	Line v[maxp];   //å¤è¾¹å½¢çè¾¹
};

function.block:
{  //ç¹ptï¼å¤è¾¹å½¢Point *p
	for (int i = 0; i < n; i++)
	{                    //ç¹å¨å¤è¾¹å½¢çé¡¶ç¹ä¸
		if (p[i] == pt) return 3;
	}
	for (int i = 0; i < n; i++)
	{  //ç¹å¨å¤è¾¹å½¢çè¾¹ä¸
		Line v = Line(p[i], p[(i + 1) % n]);
		if (Point_on_seg(pt, v)) return 2;
	}
	int num = 0;
	for (int i = 0; i < n; i++)
	{
		int j = (i + 1) % n;
		int c = sgn(Cross(pt - p[j], p[i] - p[j]));
		int u = sgn(p[i].y - pt.y);
		int v = sgn(p[j].y - pt.y);
		if (c > 0 && u < 0 && v >= 0) num++;
		if (c < 0 && u >= 0 && v < 0) num--;
	}
	return num != 0;  // 1 åé¨; 0 å¤é¨
}

{  // Point *pè¡¨ç¤ºå¤è¾¹å½¢ãä»åç¹å¼å§ååä¸è§å½¢
	double area = 0;
	for (int i = 0; i < n; i++) area += Cross(p[i], p[(i + 1) % n]);
	return area / 2;  //é¢ç§¯ææ­£è´ï¼ä¸è½ç®åå°åç»å¯¹å¼
}

{  //æ±å¤è¾¹å½¢éå¿ãPoint *pè¡¨ç¤ºå¤è¾¹å½¢ã
	Point ans(0, 0);
	if (Polygon_area(p, n) == 0) return ans;
	for (int i = 0; i < n; i++)
		ans = ans +
		(p[i] + p[(i + 1) % n]) * Cross(p[i], p[(i + 1) % n]);  //é¢ç§¯ææ­£è´
	return ans / Polygon_area(p, n) / 6.;
}

unit.struct:
struct Circle
{
	Point c;   //åå¿
	double r;  //åå¾
	Circle()
	{
	}
	Circle(Point c, double r) : c(c), r(r)
	{
	}
	Circle(double x, double y, double _r)
	{
		c = Point(x, y);
		r = _r;
	}
};

function.block:
{
	double dst = Distance(p, C.c);
	if (sgn(dst - C.r) < 0) return 0;   //ç¹å¨åå
	if (sgn(dst - C.r) == 0) return 1;  //åä¸
	return 2;                           //åå¤
}

{
	double dst = Dis_point_line(C.c, v);
	if (sgn(dst - C.r) < 0) return 0;   //ç´çº¿å¨åå
	if (sgn(dst - C.r) == 0) return 1;  //ç´çº¿ååç¸å
	return 2;                           //ç´çº¿å¨åå¤
}

{  // pa, pbæ¯äº¤ç¹ãè¿åå¼æ¯äº¤ç¹ä¸ªæ°
	if (Line_circle_relation(v, C) == 2) return 0;  //æ äº¤ç¹
	Point q = Point_line_proj(C.c, v);   //åå¿å¨ç´çº¿ä¸çæå½±ç¹
	double d = Dis_point_line(C.c, v);   //åå¿å°ç´çº¿çè·ç¦»
	double k = sqrt(C.r * C.r - d * d);  //
	if (sgn(k) == 0)
	{                   // 1ä¸ªäº¤ç¹ï¼ç´çº¿ååç¸å
		pa = q;
		pb = q;
		return 1;
	}
	Point n = (v.p2 - v.p1) / Len(v.p2 - v.p1);  //åä½åé
	pa = q + n * k;
	pb = q - n * k;
	return 2;  // 2ä¸ªäº¤ç¹
}

unit.struct:
struct Point3
{
	double x, y, z;
	Point3()
	{
	}
	Point3(double x, double y, double z) : x(x), y(y), z(z)
	{
	}
	Point3 operator+(Point3 B)
	{
		return Point3(x + B.x, y + B.y, z + B.z);
	}
	Point3 operator-(Point3 B)
	{
		return Point3(x - B.x, y - B.y, z - B.z);
	}
	Point3 operator*(double k)
	{
		return Point3(x * k, y * k, z * k);
	}
	Point3 operator/(double k)
	{
		return Point3(x / k, y / k, z / k);
	}
	bool operator==(Point3 B)
	{
		return sgn(x - B.x) == 0 && sgn(y - B.y) == 0 && sgn(z - B.z) == 0;
	}
};

function.block:
{
	return A.x * B.x + A.y * B.y + A.z * B.z;
}

{
	return Point3(A.y * B.z - A.z * B.y, A.z * B.x - A.x * B.z,
		A.x * B.y - A.y * B.x);
}

{
	return sqrt(Dot(A, A));
}

{
	return Dot(A, A);
}

{
	return sqrt((A.x - B.x) * (A.x - B.x) + (A.y - B.y) * (A.y - B.y) +
		(A.z - B.z) * (A.z - B.z));
}

{
	return acos(Dot(A, B) / Len(A) / Len(B));
}

