  template<class T> istream& operator ,  (istream& is, T& val){ return is >> val;}
  template<class T, class S> ostream& operator << (ostream& os, const pair<T,S>& p){return os << p.first << " " << p.second;}
  template<class T> ostream& operator << (ostream& os, const vector<T>& vec){for(size_t i=0; i<vec.size(); i++) os << vec[i] << (i==vec.size()-1?"":" "); return os;}
  template<class T> ostream& operator ,  (ostream& os, const T& val){ return os << " " << val;}

  template<class H> void print(const H& head){ cout << head; }
  template<class H, class ... T> void print(const H& head, const T& ... tail){ cout << head << " "; print(tail...); }
  template<class ... T> void println(const T& ... values){ print(values...); cout << endl; }

  template<class H> void eprint(const H& head){ cerr << head; }
  template<class H, class ... T> void eprint(const H& head, const T& ... tail){ cerr << head << " "; eprint(tail...); }
  template<class ... T> void eprintln(const T& ... values){ eprint(values...); cerr << endl; }

  class range{ Integer start_, end_, step_; public: struct range_iterator{ Integer val, step_; range_iterator(Integer v, Integer step) : val(v), step_(step) {} Integer operator * (){return val;} void operator ++ (){val += step_;} bool operator != (range_iterator& x){return step_ > 0 ? val < x.val : val > x.val;} }; range(Integer len) : start_(0), end_(len), step_(1) {} range(Integer start, Integer end) : start_(start), end_(end), step_(1) {} range(Integer start, Integer end, Integer step) : start_(start), end_(end), step_(step) {} range_iterator begin(){ return range_iterator(start_, step_); } range_iterator   end(){ return range_iterator(  end_, step_); } };

  inline string operator "" _s (const char* str, size_t size){ return move(string(str)); }
  constexpr Integer my_pow(Integer x, Integer k, Integer z=1){return k==0 ? z : k==1 ? z*x : (k&1) ? my_pow(x*x,k>>1,z*x) : my_pow(x*x,k>>1,z);}
  constexpr Integer my_pow_mod(Integer x, Integer k, Integer M, Integer z=1){return k==0 ? z%M : k==1 ? z*x%M : (k&1) ? my_pow_mod(x*x%M,k>>1,M,z*x%M) : my_pow_mod(x*x%M,k>>1,M,z);}
  constexpr unsigned long long operator "" _ten (unsigned long long value){ return my_pow(10,value); }

  inline int k_bit(Integer x, int k){return (x>>k)&1;} //0-indexed

  mt19937 mt(chrono::duration_cast<chrono::nanoseconds>(chrono::steady_clock::now().time_since_epoch()).count());

  template<class T> string join(const vector<T>& v, const string& sep){ stringstream ss; for(size_t i=0; i<v.size(); i++){ if(i>0) ss << sep; ss << v[i]; } return ss.str(); }

  inline string operator * (string s, int k){ string ret; while(k){ if(k&1) ret += s; s += s; k >>= 1; } return ret; }
}

unit.class:
class UnionFindTree{
  struct base_node{
    int parent;
    int rank;
    int size;
  };

  vector<base_node> node;
 public:
  UnionFindTree(int n){
    node.resize(n);
    for(int i=0; i<n; i++){
      node[i].parent=i;
      node[i].rank=0;
      node[i].size=1;
    }
  }

  int find(int x){  //return root node of x
    if(node[x].parent == x) return x;
    else{
      return node[x].parent = find(node[x].parent);
    }
  }

  bool same(int x, int y){
    return find(x) == find(y);
  }

  int size(int at){
    return node[find(at)].size;
  }

  void unite(int x, int y){
    x = find(node[x].parent);
    y = find(node[y].parent);

    if(x==y) return;

    if(node[x].rank < node[y].rank){
      node[x].parent = y;
      node[y].size += node[x].size;
    }else if(node[x].rank > node[y].rank){
      node[y].parent = x;
      node[x].size += node[y].size;
    }else{
      node[x].rank++;
      unite(x,y);
    }
  }
};

function.block:
{
  int n;
  cin >> n;
  vector<pair<int,int>> p(n);
  vector<long long> c(n), k(n);
  cin >> p, c, k;

  long long inf = 1ll<<58;
  UnionFindTree uft(n);
  vector<pair<long long, pair<int,int>>> e;
  for(int i=0; i<n; i++){
    int xi, yi;
    tie(xi, yi) = p[i];
    for(int j=i+1; j<n; j++){
      int xj, yj;
      tie(xj, yj) = p[j];
      long long w = k[i] + k[j];
      long long d = abs(xi-xj) + abs(yi-yj);
      e.emplace_back(w*d, make_pair(i,j));
    }
  }
  sort(e.begin(), e.end());

  vector<long long> cmin = c;
  long long now = accumulate(c.begin(), c.end(), 0ll);
  vector<pair<int,int>> edges;
  for(int h=0; h<e.size(); h++){
    long long w;
    int i,j;
    w = e[h].first;
    tie(i,j) = e[h].second;
    int ri = uft.find(i);
    int rj = uft.find(j);
    if(ri == rj) continue;

    long long c_ = max(cmin[ri], cmin[rj]);
    long long diff = w - c_;
    if(diff < 0){
      now += diff;
      uft.unite(ri, rj);
      edges.emplace_back(i+1, j+1);
      int r = uft.find(ri);
      cmin[r] = min(cmin[ri], cmin[rj]);

    }
  }

  println(now);

  map<int, pair<long long,int>> root;
  for(int i=0; i<n; i++){
    int r = uft.find(i);
    if(root.count(r) == 0){
      root[r] = make_pair(c[i], i);
    }else{
      root[r] = min(root[r], make_pair(c[i], i));
    }
  }

  println(root.size());
  vector<int> roots;
  for(auto p: root){
    roots.push_back(p.second.second + 1);
  }
  println(join(roots, " "));
  println(edges.size());
  println(join(edges, "\n"));

  return 0;
}
